import React, { Component } from 'react';


class Team extends Component {
    state = { 
        name:'Team',
        gamesPlayed: 0,
        goalDifference: 0,
        points: 0
     }
     constructor(){
         super();
         this.addThreePoints = this.addThreePoints.bind(this);
         this.addOnePoint= this.addOnePoint.bind(this);
         this.increaseGd = this.increaseGd.bind(this);
         this.decreaseGd = this.decreaseGd.bind(this);

     }

     addThreePoints(){
         if(this.state.gamesPlayed < 31){
            this.setState({points: this.state.points+3});
            this.setState({gamesPlayed: this.state.gamesPlayed +1})
         }   
     }
     addOnePoint(){
         if(this.state.gamesPlayed < 31){
            this.setState({points: this.state.points+1});
            this.setState({gamesPlayed: this.state.gamesPlayed +1})

        }
    }
    increaseGd(){
        this.setState({goalDifference:this.state.goalDifference+1});  
    }
    decreaseGd(){
        this.setState({goalDifference:this.state.goalDifference-1});  
    }

    render() { 
        return ( 
            <ul class="list-group list-group-horizontal">
                <li className="list-group-item">{this.state.name}</li>
                <li className="list-group-item">{this.state.gamesPlayed}</li>
                <li className="list-group-item">{this.state.goalDifference}
                <button onClick={this.increaseGd} className="btn btn-success">+</button>
                <button onClick={this.decreaseGd}className="btn btn-danger">-</button>
                </li>
                <li className="list-group-item">{this.state.points}</li>
                <li className="list-group-item">
                    <button 
                        onClick = {this.addThreePoints}
                        className="btn btn-success">
                            3 Points
                    </button>
                </li>
                <li className="list-group-item">
                    <button 
                        onClick = {this.addOnePoint}
                        className="btn btn-info">
                            1 Points
                    </button>
                </li>
            </ul>
         );
    }
}

export default Team;
