import React, { Component } from 'react';
import NavBar from './components/navbar';
import './App.css';
import Teams from './components/teams';
import Team from './components/team'

class App extends Component {
  state = { 
    teams: [

    ],
  }

  addNewTeam = () =>{
      if(this.state.teams.length < 15){
        this.setState(prevState => ({teams:[...prevState.teams,{name:'',pl:0,gd:0,pts:0}]}));
      }
  }
  render() { 
    return (
      <div>
        <NavBar/>
        <div className="container">
          <ul class="list-group list-group-horizontal">
            <li class="list-group-item list-group-item-primary">Team</li>
            <li class="list-group-item list-group-item-primary">Pl</li>
            <li class="list-group-item list-group-item-primary">GD</li>
            <li class="list-group-item list-group-item-primary">Pts</li>
          </ul>
          {this.state.teams.map(team => <Team/>)}
          <button onClick = {this.addNewTeam} className='btn btn-primary'> Add New Team</button>
        </div>
      </div>
     );
  }
}

export default App;
